import { Component, Input, OnInit } from '@angular/core';
import { NgbActiveModal } from '@ng-bootstrap/ng-bootstrap';

@Component({
  selector: 'app-editar-estudiante-modal',
  templateUrl: './editar-estudiante-modal.component.html',
  styleUrls: ['./editar-estudiante-modal.component.css']
})
export class EditarEstudianteModalComponent implements OnInit {
  estudiantes: Estudiante[] = [];
  nuevoEstudiante: Estudiante = { estudianteId: 0, nombre: '', identificacion: 0 };
  @Input() estudiante: Estudiante | null = null;
  estudianteEditado: Estudiante | null = null;

  constructor(public activeModal: NgbActiveModal) { }

  ngOnInit(): void {
    if (this.estudiante) {
      // Clonar el estudiante para no modificar el original directamente
      this.estudianteEditado = { ...this.estudiante };
    }
  }

  guardarCambios() {
    if (this.estudianteEditado) {
      // Aquí debes llamar a tu servicio HTTP para guardar los cambios en el estudiante
      // this.http.put(`/api/Estudiantes/${this.estudianteEditado.estudianteId}`, this.estudianteEditado).subscribe(() => {
      //   this.activeModal.close('Guardado');
      // }, error => console.error(error));
      this.activeModal.close('Guardado');
    }
  }

  cancelarEdicion() {
    this.activeModal.dismiss('Cancelado');
  }
}

interface Estudiante {
  estudianteId: number;
  nombre: string;
  identificacion: number;
}
