import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { MatDialog } from '@angular/material/dialog';

import { ModalEditar } from '../modales/modal.component';

@Component({
  selector: 'app-estudiantes',
  templateUrl: './estudiantes.component.html',
  styleUrls: ['./estudiantes.component.css'],
})
export class EstudiantesComponent {
  estudiantes: Estudiante[] = [];
  nuevoEstudiante: Estudiante = { estudianteId: 0, nombre: '', identificacion: 0 };
  estudianteEnEdicion: Estudiante | null = null;

  constructor(private http: HttpClient, @Inject('BASE_URL') baseUrl: string, public dialog: MatDialog) {
    this.cargarEstudiantes();
  }

  cargarEstudiantes() {
    this.http.get<Estudiante[]>('/api/Estudiantes').subscribe(data => {
      this.estudiantes = data;
      console.log(this.estudiantes);
    }
      , error => console.error(error));
  }

  agregarEstudiante() {
    this.http.post('/api/Estudiantes', this.nuevoEstudiante).subscribe(() => {
      this.nuevoEstudiante = { estudianteId: 0, nombre: '', identificacion: 0 };
      this.cargarEstudiantes();
    }, error => console.error(error));
  }

  editarEstudiante(estudiante: Estudiante) {
    this.estudianteEnEdicion = { ...estudiante };
  }

  openDialog() {
    this.dialog.open(ModalEditar);
  }

  guardarCambios() {
    if (this.estudianteEnEdicion) {
      this.http.put(`/api/Estudiantes/${this.estudianteEnEdicion.estudianteId}`, this.estudianteEnEdicion).subscribe(() => {
        this.estudianteEnEdicion = null;
        this.cargarEstudiantes();
      }, error => console.error(error));
    }
  }

  cancelarEdicion() {
    this.estudianteEnEdicion = null;
  }

  eliminarEstudiante(estudiante: Estudiante) {
    this.http.delete(`/api/Estudiantes/${estudiante.estudianteId}`).subscribe(() => {
      this.cargarEstudiantes();
    }, error => console.error(error));
  }
}

interface Estudiante {
  estudianteId: number;
  nombre: string;
  identificacion: number;
}
<h1>Lista de Estudiantes</h1>
<table class="table">
  <thead>
    <tr>
      <th>ID</th>
      <th>Nombre</th>
      <th>Identificación</th>
      <th>Acciones</th>

    </tr>
  </thead>
  <tbody>
    <ng-container *ngFor="let estudiante of estudiantes">
      <tr>
        <td>{{ estudiante.estudianteId }}</td>
        <td *ngIf="estudianteEnEdicion !== estudiante">{{ estudiante.nombre }}</td>
        <td *ngIf="estudianteEnEdicion !== estudiante">{{ estudiante.identificacion }}</td>
        <td *ngIf="estudianteEnEdicion === estudiante">
          <input type="text" [(ngModel)]="estudianteEnEdicion.nombre">
        </td>
        <td>
          <ng-container *ngIf="estudianteEnEdicion === estudiante; else acciones">
            <button (click)="guardarCambios()" class="btn btn-success">Guardar</button>
            <button (click)="cancelarEdicion()" class="btn btn-secondary">Cancelar</button>
          </ng-container>
          <ng-template #acciones>
            <button mat-button (click)="openDialog()">Launch dialog</button>
            <!--<button (click)="editarEstudiante(estudiante)" class="btn btn-primary">Editar</button>-->
            <button (click)="eliminarEstudiante(estudiante)" class="btn btn-danger">Eliminar</button>
          </ng-template>

        </td>
      </tr>
    </ng-container>
  </tbody>
</table>
<div class="form-group">
  <div class="contenedor-entradas-datos">
    <div>
      <label for="nombre">Nombre:</label>
      <input type="text" class="form-control" id="nombre" [(ngModel)]="nuevoEstudiante.nombre" name="nombre" required>
    </div>
    <div>
      <label for="nombre">Identificación:</label>
      <input type="text" class="form-control" id="nombre" [(ngModel)]="nuevoEstudiante.identificacion" name="nombre" required>
    </div>
  </div>
</div>
<button (click)="agregarEstudiante()" class="btn btn-success">Agregar</button>
